import{_ as e,o as a,c as t,a as i}from"./app.fab02784.js";const b=JSON.parse('{"title":"FAQ","description":"","frontmatter":{"title":"FAQ","sidebarDepth":1,"lastUpdated":true,"sitemap":{"priority":0.6}},"headers":[{"level":2,"title":"Windows users use","slug":"windows-users-use"},{"level":2,"title":"Cannot find command after global install","slug":"cannot-find-command-after-global-install"},{"level":2,"title":"Terminal cannot display Emoji symbols","slug":"terminal-cannot-display-emoji-symbols"},{"level":2,"title":"What is different between cz-git and czg","slug":"what-is-different-between-cz-git-and-czg"}],"relativePath":"faq/index.md","lastUpdated":1658542054000}'),n={name:"faq/index.md"},o=i('<h1 id="faq" tabindex="-1">FAQ <a class="header-anchor" href="#faq" aria-hidden="true">#</a></h1><h2 id="windows-users-use" tabindex="-1">Windows users use <a class="header-anchor" href="#windows-users-use" aria-hidden="true">#</a></h2><ul><li>Windows users are advised not to use powershell, cmd for command line use</li><li>because they are not based on the POSIX SHELL specification, i.e. not a standard terminal environment</li><li>It is recommended not to use gitbash, because the terminal is not an interactive terminal, and the selection up and down will be effected</li><li><strong>It is recommended to use Windows Terminal combined with WSL, you should also do the same in daily development and use</strong></li></ul><h2 id="cannot-find-command-after-global-install" tabindex="-1">Cannot find command after global install <a class="header-anchor" href="#cannot-find-command-after-global-install" aria-hidden="true">#</a></h2><ul><li>Enter the command <code>npm prefix -g</code> to check whether the path of npm global download is in the root directory</li><li>The high probability is because the global download path prefix of npm has been changed with nvm</li><li>You can open .zshrc or .bashrc to comment out the loading nvm, and then reopen the terminal to check</li></ul><h2 id="terminal-cannot-display-emoji-symbols" tabindex="-1">Terminal cannot display Emoji symbols <a class="header-anchor" href="#terminal-cannot-display-emoji-symbols" aria-hidden="true">#</a></h2><ul><li>The terminal cannot Emoji symbols, the high probability is because your terminal has poor support for emoji/unicode characters, but it does not affect the submission Because the final output is submitted by Emoji Code, you can consider changing the terminal and <a href="https://github.com/ryanoasis/nerd-fonts" target="_blank" rel="noopener noreferrer">font</a></li></ul><h2 id="what-is-different-between-cz-git-and-czg" tabindex="-1">What is different between <code>cz-git</code> and <code>czg</code> <a class="header-anchor" href="#what-is-different-between-cz-git-and-czg" aria-hidden="true">#</a></h2><blockquote><p>See more <a href="/cli/why.html">Why czg</a></p></blockquote><ul><li>If you using <code>cz</code> or <code>git cz</code> command will start <code>commitizen</code> CLI <mark>+</mark> <code>cz-git</code> adapter</li><li>If you using <code>czg</code> command will only start <code>czg</code> CLI</li></ul>',10),s=[o];function d(l,r,c,h,m,u){return a(),t("div",null,s)}var p=e(n,[["render",d]]);export{b as __pageData,p as default};
